import React from 'react'
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import axios from 'axios'
import { useLocation, useNavigate } from 'react-router-dom';
import moment from "moment"

const Write = () => {

  const state = useLocation().state
  const [value, setValue] = React.useState(state?.title || '');
  const [title, setTitle] = React.useState(state?.desc || '');
  const [file, setFile] = React.useState(null);
  const [cat, setCat] = React.useState(state?.cat || "");

  const navigate = useNavigate()


  const upload = async ()=> {
    try{
      const formData = new FormData()
      formData.append('file', file)
      const res = await axios.post('/upload', formData)
      return res.data
    }catch(err){
      console.log(err)
    }
  }

  const handleClick = async e => {
    e.preventDefault();
    const imgUrl = await upload()
    try{
      state ? await axios.put(`/posts/${state.id}`, {
        title,
        desc:value,
        cat,
        img:file ? imgUrl : ""
      }) 
      : await axios.post(`/posts/`, {
        title,
        desc:value,
        cat,
        img:file ? imgUrl : "",
        date: moment(Date.now()).format("YYYY-MM-DD HH:mm:ss")
      })
      navigate('/')

    }catch(err){
      console.log(err)
    }
  }

  return (
    <div className='add'>
      <div className='content'>
        <input type="text" value={title} placeholder='Title' onChange={e=>setTitle(e.target.value)}></input>
        <div className="editorContainer">
          <ReactQuill className='editor'theme="snow" value={value} onChange={setValue} />
        </div>
      </div>
      <div className='menu'>
        <div className='item'>
          <h1>Publish</h1>
          <span>
            <b>Status: </b> Draft
          </span>
          <span>
            <b>Visibility: </b> Public
          </span>
          <input style={{display:"none"}}type='file' id='file' name='' onChange={e=>setFile(e.target.files[0])}/>
          <label className='file' htmlFor='file'>Upload Image</label>
          <div className='buttons'>
            <button>Save as a draft</button>
            <button onClick={handleClick}>Publish</button>
          </div>
        </div>
        <div className='item'>
          <h1>Categories</h1>
          <div className="cat">
          <input type="radio" checked = {cat === "art"} name="cat" value="art" id="art" onChange={e=>setCat(e.target.value)}></input>
          <label htmlFor="art">Art</label>
          </div>
          <div className="cat">
          <input type="radio" checked = {cat === "science"} name="cat" value="science" id="science" onChange={e=>setCat(e.target.value)}></input>
          <label htmlFor="science">Science</label>
          </div>
          <div className="cat">
          <input type="radio" checked = {cat === "tech"} name="cat" value="tech" id="tech" onChange={e=>setCat(e.target.value)}></input>
          <label htmlFor="tech">Tech</label>
          </div>
          <div className="cat">
          <input type="radio" checked = {cat === "food"} name="cat" value="food" id="food" onChange={e=>setCat(e.target.value)}></input>
          <label htmlFor="food">Food</label>
          </div>
        </div>
      </div>
    </div>
  )
}

export default Write
