import Edit from '../img/edit.png'
import Delete from '../img/delete.png'
import { Link } from 'react-router-dom'
import Menu from '../components/Menu'

const Single = () => {
  const [post,setPost] = useState([])

  const cat = useLocation().search

  useEffect(() => {
    const fetchData = async () => {
      try{
        const res = await axios.get(`/posts${cat}`)
        setPosts(res.data)
      }catch(err){
        console.log(err)
      }
    }
    fetchData()
  }, [cat])
  return (
    <div className='single'>
      <div className="content">
        <img src="https://i.scdn.co/image/ab67616d00001e02081bf070caf4e8a13da71f77" alt="" />
      <div className="user">
        <img src="https://i.scdn.co/image/ab67616100005174ef0ccb5725c4881b80ec52b5" alt="" />
      <div className="info">
        <span>John</span>
        <p>1 hour ago</p>
      </div>
      <div className='edit'>
        <Link to = {`/write?edit=2`}>
          <img src={Edit} alt='' />
        </Link>
        <img src={Delete} alt='' />
      </div>
      </div>
      <h1>Lorem ipsum </h1>
      <p>Lorem ipsum dolor, sit amet consectetur adipisicing elit. Officiis id ratione quos reprehenderit aspernatur quibusdam veritatis provident, iste, commodi accusamus eius autem architecto dolore? Sequi omnis quibusdam neque possimus reiciendis.</p>
      </div>
      <Menu />
    </div>
  )
}

export default Single
